import { __decorate } from "tslib";
import { Component, Input } from '@angular/core';
import { take, map } from 'rxjs';
let ServiceComponent = class ServiceComponent {
    constructor(http) {
        this.http = http;
        this.stateServ = 'list';
        this.stepperState = 1;
        this.qrdata = "";
        this.proxyHeroku = "https://radiant-harbor-95836.herokuapp.com/";
        this.base_url = this.proxyHeroku + 'https://17d6-186-84-135-86.ngrok.io';
        this.completed = false;
    }
    ngOnInit() {
        this.qrdata = 'Initial QR code data string';
    }
    changeStepperState(state) {
        this.stepperState = state;
    }
    changeStateServ(state) {
        this.stateServ = state;
    }
    async createDataService(caseOfUse) {
        console.log(this.base_url + '/api/sign-in');
        this.http.get(this.base_url + '/api/sign-in', { params: { caseOfUse } }).pipe(take(1), map((response) => {
            console.log(response);
            this.qrdata = JSON.stringify(response);
            console.log(this.qrdata);
            this.completed = true;
        })).subscribe();
    }
};
__decorate([
    Input()
], ServiceComponent.prototype, "user", void 0);
ServiceComponent = __decorate([
    Component({
        selector: 'app-service',
        templateUrl: './service.component.html',
        styleUrls: ['./service.component.scss']
    })
], ServiceComponent);
export { ServiceComponent };
